Clear-Host; New-Item -ItemType "directory" encoded 2>&1>$null; Get-ChildItem *.mkv| ForEach-Object {Write-Host "Starting $($_.Name)`n"; cmd /k "vspipe ME!ME!ME!.vpy --arg `"src_path=$($_.Name)`" -y - | ffmpeg -y -hide_banner -loglevel warning -stats -i pipe: -i `"$($_.Name)`" -map 1:t? -map 0:v -map 1:a -map 1:s -c:v libx264 -preset veryslow -fastfirstpass false -crf 16 -aq-mode 3 -aq-strength 0.80 -psy-rd 0.70 -rc-lookahead 60 -deblock 1.0:0.0 -x264-params `"no-dct-decimate=1:no-fast-pskip=1:merange=32:bframes=16:ref=16:keyint=480:min-keyint=23:qcomp=0.70`" -c:a copy -c:s copy `"encoded\$($_.BaseName).mkv`" & exit"; Write-Host "`n----------------`n"}