Clear-Host; New-Item -ItemType "directory" encoded 2>&1>$null; Get-ChildItem *.mkv| ForEach-Object {Write-Host "Starting $($_.Name)`n"; cmd /k "vspipe Yaiba.vpy --arg `"src_path=$($_.Name)`" -y - | ffmpeg -y -hide_banner -loglevel warning -stats -i pipe: -i `"$($_.Name)`" -map 0:v -map 1:a:0 -map 1:s? -pix_fmt yuv420p10le -c:v libx265 -preset slow -x265-params `"log-level=error:aq-strength=0.70:psy-rd=1.0:psy-rdoq=1.0:aq-mode=3:qcomp=0.60:ref=6:bframes=8:crf=22.5:deblock=0,0:subme=5:amp=1:rect=1:frame-threads=2`" -c:a copy -c:s copy `"encoded\$($_.BaseName).mkv`" & exit"; Write-Host "`n----------------`n"}