Clear-Host; New-Item -ItemType "directory" encoded 2>&1>$null; Get-ChildItem *.mkv| ForEach-Object {Write-Host "Starting $($_.Name)`n"; cmd /k "vspipe Sakurasou.vpy --arg `"src_path=$($_.Name)`" -y - | ffmpeg -y -hide_banner -loglevel warning -stats -i pipe: -i `"$($_.Name)`" -map 1:t? -map 0:v -map 1:a -map 1:s? -c:v libx264 -preset veryslow -x264-params `"rc-lookahead=250:b-adapt=2:direct=auto:psy-rd=0.75,0:aq-mode=3:no-dct-decimate=1:crf=14:no-fast-pskip=1:merange=48:bframes=16:ref=16:qcomp=0.70:aq-strength=0.9:me=tesa:deblock=-2,-1:min-keyint=24:mixed-refs=1`" -c:a libopus -b:a 192k -ac 2 -c:s copy `"encoded\$($_.BaseName).mkv`" & exit"; Write-Host "`n----------------`n"}