Clear-Host; New-Item -ItemType "directory" encoded 2>&1>$null; Get-ChildItem *.mkv| ForEach-Object {Write-Host "Starting $($_.Name)`n"; cmd /k "vspipe Sakurasou.vpy --arg `"src_path=$($_.Name)`" -y - | ffmpeg -y -hide_banner -loglevel warning -stats -i pipe: -i `"$($_.Name)`" -map 1:t? -map 0:v -map 1:a? -map 1:s? -c:v libx264 -preset veryslow -x264-params `"rc-lookahead=40:b-adapt=2:direct=auto:psy-rd=1.00,0:aq-mode=3:no-dct-decimate=1:crf=14.5:no-fast-pskip=1:merange=48:bframes=16:ref=16:qcomp=0.70:no-mbtree=1:aq-strength=0.90:me=tesa:deblock=-2,-2:min-keyint=24:mixed-refs=1:zones=257,339,crf=10/5965,6015,crf=10/9984,10128,crf=10/10247,10586,crf=10/18797,18894,crf=10/20988,21633,crf=10/24020,24257,crf=10`" -c:a libopus -b:a 192k -ac 2 -c:s copy `"encoded\$($_.BaseName).mkv`" & exit"; Write-Host "`n----------------`n"}