Clear-Host; New-Item -ItemType "directory" encoded 2>&1>$null; Get-ChildItem *.m2ts| ForEach-Object {Write-Host "Starting $($_.Name)`n"; cmd /k "vspipe Wataten.vpy --arg `"src_path=$($_.Name)`" -y - | ffmpeg -y -hide_banner -loglevel warning -stats -i pipe: -i `"$($_.Name)`" -map 0:v -map 1:a:0 -map 1:s? -pix_fmt yuv420p10le -c:v libx265 -preset slow -x265-params `"log-level=error:rc-lookahead=40:tu-intra-depth=3:tu-inter-depth=3:weightb=1:b-intra=1:aq-strength=0.80:psy-rd=1.0:psy-rdoq=1.2:me=2:aq-mode=3:qcomp=0.60:ref=6:bframes=12:crf=21:deblock=0,0:fades=1:subme=4:gop-lookahead=0:amp=1:crqpoffs=-1:cbqpoffs=-1:frame-threads=1:no-cutree=1:qp-adaptation-range=3`" -c:a libopus -b:a 112k -ac 2 -c:s copy `"encoded\$($_.BaseName).mkv`" & exit"; Write-Host "`n----------------`n"}