Clear-Host; New-Item -ItemType "directory" encoded 2>&1>$null; Get-ChildItem *.mkv| ForEach-Object {Write-Host "Starting $($_.Name)`n"; cmd /k "vspipe ReZeroS2.vpy --arg `"src_path=$($_.Name)`" -y - | ffmpeg -y -hide_banner -loglevel warning -stats -i pipe: -i `"$($_.Name)`" -map 0:v -map 1:a:0 -map 1:s -pix_fmt yuv420p10le -c:v libx265 -preset slow -x265-params `"log-level=error:rc-lookahead=60:tu-intra-depth=3:tu-inter-depth=3:weightb=1:b-intra=1:aq-strength=0.75:psy-rd=1.2:psy-rdoq=2:aq-mode=3:qcomp=0.70:ref=6:bframes=16:crf=21:deblock=1,-1:crqpoffs=-2:cbqpoffs=-1:no-cutree=1:hevc-aq=1:qp-adaptation-range=3:fades=1:subme=5:amp=1:frame-threads=2:me=umh`" -c:a copy -c:s copy `"encoded\$($_.BaseName).mkv`" & exit"; Write-Host "`n----------------`n"}