Clear-Host; New-Item -ItemType "directory" encoded 2>&1>$null; Get-ChildItem *.mkv| ForEach-Object {Write-Host "Starting $($_.Name)`n"; cmd /k "vspipe HxEros.vpy --arg `"src_path=$($_.Name)`" -y - | ffmpeg -y -hide_banner -loglevel warning -stats -i pipe: -i `"$($_.Name)`" -map 1:t? -map 0:v -map 1:a? -map 1:s? -c:v libx264 -preset veryslow -x264-params `"rc-lookahead=60:b-adapt=2:direct=auto:psy-rd=1.00,0:aq-mode=3:no-dct-decimate=1:crf=14.5:no-fast-pskip=1:merange=48:bframes=16:ref=16:qcomp=0.70:no-mbtree=1:aq-strength=1.00:me=tesa:deblock=-2,-2:min-keyint=24:mixed-refs=1:zones=13540,14000,crf=12.5/24570,25228,crf=13.5/27823,29450,crf=13.5/31131,33925,crf=12.5`" -c:a libopus -b:a 192k -ac 2 -c:s copy `"encoded\$($_.BaseName).mkv`" & exit"; Write-Host "`n----------------`n"}