Clear-Host; New-Item -ItemType "directory" encoded 2>&1>$null; Get-ChildItem *.mkv| ForEach-Object {Write-Host "Starting $($_.Name)`n"; cmd /k "vspipe AssaultLoli.vpy --arg `"src_path=$($_.Name)`" -y - | ffmpeg -y -hide_banner -loglevel warning -stats -i pipe: -i `"$($_.Name)`" -map 1:t? -map 0:v -map 1:a? -map 1:s? -c:v h264_nvenc -preset hq -profile:v high -cq:v 25 -b:v 2000 -maxrate:v 5.8M -bufsize:v 5M -rc-lookahead:v 60 -spatial_aq:v 1 -aq-strength:v 1 -coder:v cabac -bf:v 4 -refs:v 16 -c:a copy -c:s copy `"encoded\$($_.BaseName).mkv`" & exit"; Write-Host "`n----------------`n"}