Clear-Host; New-Item -ItemType "directory" encoded 2>&1>$null; Get-ChildItem *.mkv| ForEach-Object {Write-Host "Starting $($_.Name)`n"; cmd /k "vspipe Oregairu.vpy --arg `"src_path=$($_.Name)`" -y - | ffmpeg -y -hide_banner -loglevel warning -stats -i pipe: -i `"$($_.Name)`" -map 0:v -map 1:a:0 -pix_fmt yuv420p10le -c:v libx265 -preset slow -x265-params `"log-level=error:rc-lookahead=100:aq-strength=1.0:psy-rd=1.0:psy-rdoq=1:aq-mode=3:qcomp=0.65:ref=6:bframes=8:crf=18.5:no-sao=1:deblock=0,0:no-cutree=1:subme=5:fades=1:me=umh:frame-threads=2`" -c:a copy `"encoded\$($_.BaseName).mkv`" & exit"; Write-Host "`n----------------`n"}