Clear-Host; New-Item -ItemType "directory" encoded 2>&1>$null; Get-ChildItem *.mkv| ForEach-Object {Write-Host "Starting $($_.Name)`n"; cmd /k "vspipe Oregairu.vpy --arg `"src_path=$($_.Name)`" -y - | ffmpeg -y -hide_banner -loglevel warning -stats -i pipe: -i `"$($_.Name)`" -map 0:v -map 1:a:0 -pix_fmt yuv420p10le -c:v libx265 -preset slow -x265-params `"log-level=error:rc-lookahead=20:aq-strength=0.80:no-cutree=1:hevc-aq=1:qp-adaptation-range=3:psy-rd=1.5:psy-rdoq=3.5:aq-mode=4:gop-lookahead=0:no-rect=1:qcomp=0.60:ref=6:bframes=8:crf=20:deblock=-1,-1:fades=1:me=3:subme=7:no-sao=1:no-strong-intra-smoothing=1:me=umh:weightb=1:frame-threads=2:crqpoffs=-1:cbqpoffs=-1`" -c:a copy `"encoded\$($_.BaseName).mkv`" & exit"; Write-Host "`n----------------`n"}