Clear-Host; New-Item -ItemType "directory" encoded 2>&1>$null; Get-ChildItem *.mkv| ForEach-Object {Write-Host "Starting $($_.Name)`n"; cmd /k "vspipe Kanokari.vpy --arg `"src_path=$($_.Name)`" -y - | ffmpeg -y -hide_banner -loglevel warning -stats -i pipe: -i `"$($_.Name)`" -map 0:v -map 1:a:0 -pix_fmt yuv420p10le -c:v libx265 -preset slow -x265-params `"log-level=error:rc-lookahead=60:aq-strength=0.80:no-cutree=1:qp-adaptation-range=3:psy-rd=1.2:psy-rdoq=2.0:aq-mode=3:gop-lookahead=0:no-rect=1:qcomp=0.60:ref=6:bframes=16:crf=21.5:deblock=-1,-1:fades=1:no-sao=1:me=umh:weightb=1:frame-threads=3:crqpoffs=-1:cbqpoffs=-1`" -c:a copy `"encoded\$($_.BaseName).mkv`" & exit"; Write-Host "`n----------------`n"}