Clear-Host; New-Item -ItemType "directory" encoded 2>&1>$null; Get-ChildItem *.mkv| ForEach-Object {Write-Host "Starting $($_.Name)`n"; cmd /k "vspipe MaouGakuin.vpy --arg `"src_path=$($_.Name)`" -y - | ffmpeg -y -hide_banner -loglevel warning -stats -i pipe: -i `"$($_.Name)`" -map 0:v -map 1:a:0 -pix_fmt yuv420p10le -c:v libx265 -preset slow -x265-params `"log-level=error:rect=1:rc-lookahead=60:aq-strength=0.75:no-cutree=1:qp-adaptation-range=3:psy-rd=1.0:psy-rdoq=1.5:aq-mode=3:gop-lookahead=0:qcomp=0.60:ref=6:bframes=16:crf=22.5:deblock=-1,-1:no-sao=1:weightb=1:frame-threads=3:crqpoffs=-2:cbqpoffs=-2:amp=1`" -c:a copy `"encoded\$($_.BaseName).mkv`" & exit"; Write-Host "`n----------------`n"}